var util = require('util');
var path = require('path');
var _ = require('./../../node_modules/underscore');

function Config(values) {
    if (values) {
        _.extend(this, values);
    }
    var nuby_strata = require('./..');

    if (!this.app_path && this.ROOT) {
        this.app_path = this.ROOT + '/apps';
    }
}

Config.prototype = {
    ROOT:'',
    MONGODB:{
        SERVER_NAME:'localhost',
        DB_NAME:'nuby_strata',
        PORT:27017
    },
    default_lang:'us_en',
    lang: '',
    app_config:{port:3000},
    static_resources: []
}

module.exports = {
    Config:Config,
    create:function (values) {
       // console.log('config::create root = %s', util.inspect(values));
        return new Config(values);
    }
}