var path = require('path');

exports = function(app_path, config, app, app_info) {

    if (!app_info){
        app_info = require(app_path);
    }

    var app_config = {
        module: app_info,
        path: app_path,
        config: config
    }

    var get_path = app_path + '/home';

    if (path.existsSync(get_path )){
        var cb = require(get_path);
        app.get('/',              cb.handle(app_config));
        app.get('/index.:format', cb.handle(app_config, 'format'));
    }

}