var nuby_strata = require('../../../../../../lib');
var smr = nuby_strata.render;
var util = require('util');

module.exports = {

    handle:function (config, type) {
        var def_handler = nuby_strata.loader.loader('default');
       // console.log('simple_server::handle - handling with default handler: ', util.inspect(def_handler, false, 0));
        return def_handler.scaffolding['default'].handle(config, type);
    },

    data: function(env, callback){
        callback({foo: 1});
    },

    render:function (params, callback) {
     //   console.log('rendering params', util.inspect(params));
     //   console.log('smr: %s', util.inspect(smr));
        return smr(__dirname + '/view.html', params, callback);
    }

}